import { useGlobalContext } from '../context';

import React, { useState, Fragment } from "react";
import { nanoid } from "nanoid";
import "./../App.css";
// import data from "./mock-data.json";
import ReadOnlyRow from "./../components/ReadOnlyRow";
import EditableRow from "./../components/EditableRow";
import DeleteOutlinedIcon from '@mui/icons-material/DeleteOutlined';
import EditIcon from '@mui/icons-material/Edit';
import { Grid } from '@mui/material';

const CRUDTable = () => {
  const {roles, deleteRole} = useGlobalContext()

  console.log("ROLEESS", roles)

  const [rolesss, setRoles] = useState(roles);

  const [addFormData, setAddFormData] = useState({
    role_name: ""
  });

  const [editFormData, setEditFormData] = useState({
    fullName: "",
    address: "",
    phoneNumber: "",
    email: "",
  });

  const [editContactId, setEditContactId] = useState(null);

  const handleAddFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;

    const newFormData = { ...addFormData };
    newFormData[fieldName] = fieldValue;

    setAddFormData(newFormData);
  };
  const handleAddFormSubmit = (event) => {
    event.preventDefault();

    const newRole = {
      id: nanoid(),
      role_name: addFormData.role_name
    };

    const newRoles = [...rolesss, newRole];
    setRoles(newRoles);
  };




  
    return (
      <div className="app-container">

          <table>
            <thead>
              <tr>
                <th>Role Name</th>
                <th>Role Id Delete</th>
                <th>Role Id Edit</th>

              </tr>
            </thead>
            <tbody>
            {roles.map((role) => (
              <tr>
                {role.deleted === 'no' &&  (
                  <>
                  <td>
                  {role.role_name}
              </td>
              <td>
              <Grid item xs={8}>
                  {role.role_id}
                  <DeleteOutlinedIcon onClick={() => deleteRole(role.role_id)}/>
              </Grid>
              </td>
              <td>
              <Grid item xs={8}>
                  {role.role_id}
                  <EditIcon/>
              </Grid>
              </td>
              </>
                )
                }
              </tr>
              

               
                
                
            ))}
            </tbody>
          </table>
      
      </div>
    );
  };
  

export default CRUDTable
